<?php

if (! function_exists('activate_faqepage_addon')) {
    function activate_faqepage_addon($StoreThemes, $shop) {
        foreach ($StoreThemes as $theme) {

            // add snippet
            try{
                $snippet = (string) '{%- comment -%}Please do not edit this file. Any modification can be lost as it is automatically updated by Debutify{%- endcomment -%}
{%- if section.blocks.size > 0 -%}

{% assign first_tab = false %}

<script src="{{ \'jquery-ui.min.js\' | asset_url }}" defer="defer"></script>
<script src="{{ \'jquery.ui.autocomplete.scroll.min.js\' | asset_url }}" defer="defer"></script>

<div id="FaqPage"
     data-section-id="{{ section.id }}"
     data-section-type="faq-page"
     data-sticky-header="{{settings.sticky_header}}">
  <div class="grid">
    {% if section.settings.dbtfy_faq_searchbox %}
    <div class="grid__item large--one-third push--large--one-third text-center">
      <input type="text" placeholder="{{ section.settings.dbtfy_faq_search_placeholder }}" id="FaqSearch">
    </div>
    {% endif %}

    <div class="grid__item large--eight-twelfths push--large--two-twelfths clear" >
      <!-- Blocks -->
      {% for block in section.blocks %}
        {% if block.type == "category_block" %}
      	  {% unless block.settings.dbtfy_faq_category == blank %}
          <h3 class="fp-category-title">
            {% unless block.settings.dbtfy_faq_icon == blank %}
            <span class="fas fa-{{ block.settings.dbtfy_faq_icon }} fa-fw"></span>
            {% endunless %}
            {{ block.settings.dbtfy_faq_category }}
          </h3>
          {% endunless %}
        {% else %}
          <div class="fp-item" data-question="{{ block.settings.title }}" {{ block.shopify_attributes }}>
            <div class="fp-header-wrapper fp-header-wrapper-{{ block.id }} {% if first_tab == false and section.settings.dbtfy_faq_page_first %}active{% endif %}">
              <span class="fp-header-title">{{ block.settings.title }}</span>
            </div>
            <div class="fp-content-wrapper">
              <div class="fp-content rte animated faster">
              {{ block.settings.text }}
              </div>
            </div>
          </div>
          {% assign first_tab = true %}
        {% endif %}
      {% endfor %}
    </div>

    {% if section.settings.dbtfy_faq_bannerbox %}
    <div class="grid__item large--eight-twelfths push--large--two-twelfths">
      <div class="fp-banner">
        {% unless section.settings.dbtfy_faq_banner_text == blank %}
          <h4 class="fp-banner-title">{{section.settings.dbtfy_faq_banner_text}}</h4>
        {% endunless %}
        {% unless section.settings.dbtfy_faq_btn == blank %}
        <a class="btn btn-outline-primary btn--small fp-banner-button" href="{{section.settings.dbtfy_faq_link}}">{{section.settings.dbtfy_faq_btn}}</a>
        {% endunless %}
      </div>
    </div>
    {% endif %}

  </div>
</div>
{%- endif -%}
';
$snippet_schema = (string) '{% schema %}
  {
    "name": "FAQ",
    "class": "faq-section dbtfy dbtfy-faq_page",
    "max_blocks": 30,
    "settings": [
      {
        "type": "header",
        "content":"Search"
	  },
	  {
        "type": "checkbox",
        "id": "dbtfy_faq_searchbox",
        "label": "Show search",
        "default": true
      },
	  {
        "type": "text",
        "id": "dbtfy_faq_search_placeholder",
        "label":"Placeholder",
		"default": "Search our questions..."
      },
	  {
        "type": "header",
        "content":"Tabs"
	  },
      {
        "type": "checkbox",
        "id": "dbtfy_faq_page_first",
        "label": "Open first question",
        "default": false
      },
	  {
        "type": "header",
        "content":"Banner"
	  },
      {
        "type": "checkbox",
        "id": "dbtfy_faq_bannerbox",
        "label": "Show banner",
        "default": true
      },
	  {
        "type": "text",
        "id": "dbtfy_faq_banner_text",
        "label":"Banner text",
		"default": "Didn\'t find your answer?"
      },
      {
        "type": "text",
        "id": "dbtfy_faq_btn",
        "label":"Button",
		"default": "Get in Touch"
      },
      {
        "type": "url",
        "id": "dbtfy_faq_link",
        "label":"Link"
      }
    ],
    "blocks" : [
      {
        "type": "category_block",
        "name": "Category",
        "settings": [
		  {
            "type": "header",
            "content":"Category"
     	  },
          {
            "type": "text",
            "id": "dbtfy_faq_category",
            "label":"Heading",
			"default": "Category"
		  },
          {
            "type": "text",
            "id": "dbtfy_faq_icon",
            "label":"Icon",
			"default": "question-circle",
            "info": "Enter the name of any free solid icons on [FontAwesome](https:\/\/fontawesome.com\/icons?d=gallery&s=solid&m=free)"
		  }
        ]
      },
	  {
        "type": "text_block",
        "name": "Question",
        "settings": [
		  {
            "type": "header",
            "content":"Question / Answer"
     	  },
          {
            "type": "text",
            "id": "title",
            "label":"Question",
            "default": "Question"
          },
          {
            "type": "richtext",
            "id": "text",
            "label": "Answer",
            "default":"<p>This is your answer.</p>"
          }
        ]
      }
    ]
  }
{% endschema %}';

                $snippet = addScriptTagCondition($shop, '{%- comment -%}Please do not edit this file. Any modification can be lost as it is automatically updated by Debutify{%- endcomment -%}', $snippet);

                $snippet .= $snippet_schema;

                $create_trustbadge_snippet = $shop->api()->request(
                    'PUT',
                    '/admin/api/themes/'.$theme->shopify_theme_id.'/assets.json',
                    ['asset' => ['key' => 'sections/dbtfy-faq-page.liquid', 'value' => $snippet] ]
                );
            }
            catch(\GuzzleHttp\Exception\ClientException $e){
                logger('add faqepage_addon throws client exception');
            }
            catch(\Exception $e){
                logger('add faqepage_addon throws exception');
            }

            // add scss
            try{
                $styles = (string) '/* start-dbtfy-faq-page */.dbtfy-faq_page{#FaqSearch{width:100%;margin-bottom:$spacer-lg}.fp-category-title{@include screenUp($small){padding:0 $gutter-sm}@include screen($small){+ .fp-item{border-top:$borders}}}.fp-item{border-radius:$borderRadius;margin-bottom:$spacer;border:$borders;@include screen($small){border-radius:0;border-left:0;border-right:0;border-top:0;margin:0;margin-left:-$gutter-sm;margin-right:-$gutter-sm}&:last-child{margin-bottom:0}+ .fp-category-title{padding-top:$spacer;@include screen($small){padding-top:$spacer*2}}}.fp-header-wrapper{padding:$gutter-sm;cursor:pointer;@include hovers;@include transition($transitions);@include display-flexbox();@include justify-content(space-between);@include align-items(center);&:after{@include fontAwesome;content:"\f067"}}.fp-header-wrapper.active{&:after{content:"\f068"}}.fp-header-title{margin-right:$spacer-sm;margin-bottom:0;@include accentFontStack}.active .fp-header-title{color:$colorSecondary}.fp-content-wrapper{padding:$gutter $gutter-sm;display:none;border-top:$borders;.fp-content{@include animated(heroContentIn)}.rte{margin-bottom:0}}.active + .fp-content-wrapper{display:block;background-color:$colorDefault}.fp-banner{@include display-flexbox();@include align-items(center);@include justify-content(space-between);padding:$gutter/2;margin-top:$gutter;border-radius:$borderRadius;background-color:$colorDefault;@include screen($small){text-align:center;@include flex-direction(column)}}.fp-banner-title{@include screenUp($small){margin-bottom:0;margin-right:$gutter-sm}}.fp-banner-button{@include flex-shrink(0)}}.ui-menu{border-radius:$borderRadius;background-color:$colorBody;overflow:hidden;position:absolute!important;@include shadow($shadow);.ui-menu-item{list-style:none}.ui-menu-item-wrapper{cursor:pointer;border-left:$spacer-xs solid transparent;padding:$spacer-sm;@include transition($transitions);&:hover,&.ui-state-active{background-color:$colorDefault;border-color:$colorSecondary}}}.ui-helper-hidden-accessible{display:none}/* end-dbtfy-faq-page */';

                $theme_style_content = $shop->api()->request(
                    'GET',
                    '/admin/api/themes/'.$theme->shopify_theme_id.'/assets.json',
                    ['asset' => ['key' => 'assets/theme.scss.liquid'] ]
                )['body']['asset']['value'] ?? '';

                if( ( $pos = strpos( $theme_style_content , 'start-dbtfy-faq-page' ) ) === false ) {
                    $new_theme_styles = str_replace($theme_style_content, $theme_style_content.$styles, $theme_style_content);

                    $add_styles = $shop->api()->request(
                        'PUT',
                        '/admin/api/themes/'.$theme->shopify_theme_id.'/assets.json',
                        ['asset' => ['key' => 'assets/theme.scss.liquid', 'value' => $new_theme_styles] ]
                    );
                }
            }
            catch(\GuzzleHttp\Exception\ClientException $e){
                logger('update CSS on Product_tabs addon throws client exception');
            }
            catch(\Exception $e){
                logger('update CSS on Product_tabs addon throws exception');
            }

            // add snippet
            try{
              $new_prod_template ='<div class="grid">
  <div class="grid__item large--eight-twelfths push--large--two-twelfths">
    <div class="section-header">
      <h1 class="page-title">{{ page.title }}</h1>
    </div>
    {% unless page.content == blank %}
      <div class="rte">
        {{ page.content }}
      </div>
    {% endunless %}
  </div>
</div>

{% section "dbtfy-faq-page" %}';

                $update_prod_template = $shop->api()->request(
                    'PUT',
                    '/admin/api/themes/'.$theme->shopify_theme_id.'/assets.json',
                    ['asset' => ['key' => 'templates/page.faq.liquid', 'value' => $new_prod_template] ]
                );
            }
            catch(\GuzzleHttp\Exception\ClientException $e){
                logger('update Product_tabs on trustbadge addon throws client exception');
            }
            catch(\Exception $e){
                logger('update Product_tabs on trustbadge addon throws exception');
            }

            // add asset - jquery-ui.min.js
            try {
              $js = (string) '/*! jQuery UI - v1.12.1 - 2020-03-27
* http://jqueryui.com
